class Solution {
public:
  int maxOperations(string s) {
    int counter = 0;
    int result = 0;
    for (int i = 0; i < s.size(); ++i) {
      if (s[i] == '1') {
        ++counter;
        continue;
      }
      int visit = i+1;
      while (visit < s.size() && s[visit] == '0') {
        ++visit;
      }
      result += counter;
      i = visit-1;
    }
    return result;
  }
};
