class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        vector<int> a;
        int sum = 0;
        int ans = 0;
        for(int i = 0; i < s.size(); i++) {
            if(s[i] == '0') continue;
            int j = i;
            int cnt = 0;
            while(j < s.size() && s[j] == '1') {
                j++;
                cnt++;
            }

            ans += sum;
            sum += cnt;

            i = j - 1;
        }
        if(s[n - 1] == '0') ans += sum;

        return ans;
    }
};
