class Solution {
public:
    int maxOperations(string s) {
        int n = s.size(), oneCount = 0, res = 0; bool flag = false;
        for ( int i = n-2; i>=0; i-- ){
            if ( s[i] == '1' && s[i+1] == '0' ) {
                oneCount++;
                res += oneCount;
            }
            if ( s[i] == '1' && s[i+1] == '1' ) res += oneCount;
        } return res;
    }
};
