class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int ans=0;
        int l=-1;
        int p=0;
        // if(s[n-1]=='1'){
        //     l=1;
        // }
        int count=0;
        for(int i =n-2;i>=0;i--){
            if(s[i]=='1' && s[i+1]=='0'){
                count++;
                l=0;
            }
            else if(l==0 && s[i]=='1'){
                // ans+=(1);
                count++;
            }
            else{

                ans+=(count*(p+1));
                if(count>0)
                {
                    p++;
                }
                count=0;
                l=-1;
            }
        }
        ans+=(count*(p+1));
        return ans;
    }
};
