class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        vector<int> arr;
        bool flag = false;
        for(int i=n-1;i>=0;i--){
            if(s[i]=='0') flag = true;
            if(s[i]=='1' && flag){
                int j = i;
                int cnt = 0;
                while(j>=0 && s[j]=='1'){
                    cnt++;
                    j--;
                }
                arr.push_back(cnt);
                i = j+1;
            }
        }

        reverse(arr.begin(),arr.end());
        int ans = 0;
        for(int i=1;i<arr.size();i++){
            arr[i] += arr[i-1];
        }
        ans = accumulate(arr.begin(),arr.end(),0);
        return ans;
    }
};
