class Solution {
    public int maxOperations(String s) {
        int ans=0;
        char [] a= s.toCharArray();
        int n = a.length;
        boolean flag = false;
        int cnt=0;
        for(int i = n-1;i>=0;i--){
            if(a[i]=='1')ans+=cnt;
            if(a[i]=='0'){
                if(i==n-1)cnt++;
                else if(a[i+1]=='1')cnt++;
            }
        }
        return ans;
    }
}
