class Solution {
public:
    int maxOperations(string s) {
        int bridges = 0;
        long long ans = 0;
        int last = -1;
        for(int i = s.size(); i >= 0; --i){
            if(s[i] == '0' && last != 0){
                bridges++;
                last = 0;
            }
            if(s[i] == '1'){
                ans += bridges;
                last = 1;
            }
        }
        return ans;
    }
};
