class Solution {
public:
    int maxOperations(string s) {
        int operations = 0;
        int ones = 0;
        bool inGroup = false;

        for (char c : s) {
            if (c == '1') {
                ones++;
                inGroup = true;
            } else if (inGroup) {
                operations += ones;
                inGroup = false;
            }
        }

        return operations;
    }
};
