class Solution {
public:
    int maxOperations(string s) {
        int n = s.size(), ans = 0, last = n, lastmove = 0;
        for(int i = n - 1; i >= 0; i--) {
            if(s[i] == '1') {
                lastmove += (last - i > 1);
                ans += lastmove;
                last = i;
            }
        }
        return ans;
    }   
};
