class Solution {
public:
    int maxOperations(string s) {
        int tc = 0, temp = 0, f = 0;
        if(is_sorted(s.begin(),s.end())){
            return 0;
        }
        for(int i = 0; i < s.size(); ++i){
            if(s[i] == '1'){
                temp++;
                f = 1;
            }
            if(s[i] == '0' && f){
                tc += temp;
                f = 0;
            }
        }
        return tc;
    }
};
