class Solution {
public:
    int maxOperations(string s) {
        vector<int> vec; 
        long long ans=0;
        bool chk= false; 
        int pos=0, rig=0; 


        for(int i=0;i<s.size();i++){ 

            if(s[i]=='0' and chk==1){ 
                vec.push_back(0); 
                chk = false; 
            } 

            if(s[i]=='1'){ 
                vec.push_back(1); 
                chk = true; 
            } 
        } 
        for(auto x:vec){ 
            (x==1) ? pos++ : ans+=pos; 
        } 

        return ans; 

    }
};
