class Solution:
    def maxOperations(self, s: str) -> int:
        ans = 0
        p = 0
        k = False
        h = []

        for char in s:
            if char == '1':
                h.append(1)
                k = True
            if char == '0' and k:
                h.append(0)
                k = False

        for item in h:
            if item == 1:
                p += 1
            else:
                ans += p

        return ans
