class Solution {
public:
    int maxOperations(string s) {
        int ones=0;
        int ans=0;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){
                ones++;
            }
           else if( (i-1)>=0 && s[i-1]!='0' && s[i]=='0'){
                // cout<<ans<<" ";
                ans+=ones;
            }
        }
        return ans;
    }
};
