class Solution:
    def maxOperations(self, s: str) -> int:
        n  = len(s)
        i = 0
        cnt = 0
        ans = 0
        while i < n:
            if s[i] == '0':
                while i < n and s[i]=='0':
                    i+=1
                ans+= cnt
            else:
                cnt+=1
                i+=1
        return ans
