class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int countOnes = 0;

        for (int i = 0; i < s.size(); ++i) {
            if (s[i] == '1') {
                countOnes++;
            } else if ((s[i] == '0'&&s[i+1]=='1') || i == s.size() - 1) {
                ans += countOnes;
            }
        }

        return ans;
    }
};
