class Solution {
public:
    int maxOperations(string s) {
        int n = s.size(), ans = 0, c1 = 0;
        for (int i = 0; i < n; i++) {
            if (s[i] == '1') c1++;
            else {
                if (i > 0 && s[i - 1] == '1') ans += c1;
            }
        }
        return ans;
    }
};
