class Solution {
    public int maxOperations(String s) {
        int cnt1 = 0;
        int n = s.length();
        int ans = 0;
        for (int i = 0; i < n; i++) {
            if (s.charAt(i) == '1') {
                cnt1++;
                continue;
            }
            while (i<n&&s.charAt(i) == '0') {
                i++;
            }
            ans += cnt1;
            i--;
        }
        return ans;
    }
}
