class Solution {
public:
    int maxOperations(string s) {
        int last=s.length()-1,ans=0,seq=0,len=0;
       for(int i=s.length()-1;i>=0;i--){
           if(s[i]=='0'){
               len++;
           }
           else if(s[i]=='1'){
               if(len>0)
               seq++;
               len=0;
               ans+=seq;
           }
       }

        return ans;
    }
};
