class Solution:
    def maxOperations(self, s: str) -> int:
        cur_one = 0
        res = 0
        for i, n in enumerate(s):
            if n == "1":
                cur_one += 1
            else:
                if i > 0 and s[i-1] == "0":
                    continue
                else:
                    res += cur_one
        return res
