class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int ret=0;
        vector<int> groups;
        int idx=0;
        int numOnesInRow=0;
        for(int i=0;i<n;i++){
            if(s[i]=='1')numOnesInRow++;
            else{
                if(numOnesInRow!=0)groups.push_back(numOnesInRow);
                numOnesInRow=0;
            }
        }
        int cnt=0;
        for(int groupSize:groups){
            cnt+=groupSize;
            ret+=cnt;
        }
        return ret;
    }
};
