class Solution:

    def maxOperations(self, s: str) -> int:
        n, q = len(s), []
        p, st, c = 0, 0, 0
        st = int(s[0])
        if st: p = 1

        for i in range(1, n):
            if s[i] == '0':
                if st:
                    q.append(p)
                    c += p
                st = 0
                p = 0
            else:
                if not st:
                    p = 1
                    st = 1
                else: p += 1

        if not st: pass
        else:
            q.append(p)
            c += p

        ans, qn = 0, len(q)
        if st:
            for i in range(qn):
                ans += q[i] * (qn-i-1)
        else:
            for i in range(qn):
                ans += q[i] * (qn-i)

        return ans
