class Solution {
public:
    int maxOperations(string s) {
        int oc=0;
        int ans=0;
        for(int i=0;i<s.size();++i) {
            if(s[i]=='1') {
                oc++;
                if(s[i+1]=='0') {
                    ans+=oc;
                }
            }
        }
        return ans;
    }
};
