class Solution {
    public int maxOperations(String s) {
        int ans = 0;
        int n = s.length();
        int inter = 0;
        for (int i = n - 1; i >= 0; i--) {
            if (s.charAt(i) == '1') {
                if (i + 1 < n && s.charAt(i + 1) == '0') inter++;
                ans += inter;
            }
        } 
        return ans;
    }
}
