class Solution:
    def maxOperations(self, s: str) -> int:
        res = 0
        cnt = 0
        ok = False
        ls = []

        i = 0
        while i < len(s):
            if s[i] == '1':
                ls.append(1)
                ok = True
            elif s[i] == '0' and ok:
                ls.append(0)
                ok = False
            i += 1

        j = 0
        while j < len(ls):
            if ls[j] == 1:
                cnt += 1
            else:
                res += cnt
            j += 1

        return res
