class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int countOnes=0;
        bool seenZero=false;
        for(char c:s){
            if(c=='1'){
                countOnes++;
                seenZero=false;
            }else{
                if(!seenZero) ans+=countOnes;
                seenZero=true;
            }
        }
        return ans;
    }
};
