class Solution {
    public int maxOperations(String s) {
        int ans = 0;
        boolean flag = true;
        int oneCount =0;


        for(char c: s.toCharArray()){
            if(c=='1') {
                oneCount++;
                flag = true;
            }
            else {
                if(flag){
                    ans += oneCount;
                }
                flag = false;
            }
        }
        return ans;
    }
}
