class Solution {
public:
    int maxOperations(string s) {
        /*
        1. use greedy to move from begin to end, may need to scan multiple times. ( time complexity : O(n^2))
            "0011101" > "0011011" > "0010111" > "0001111"
        2. two pointer
        */
        int res = 0;
        int slen = s.size();
        int si = 0, ones = 0;
        while(si < slen){
            while((si < slen) && (s[si] == '1')) ++ ones, ++si;
            bool zflag = ((si < slen) && (s[si] == '0'));
            while((si < slen) && (s[si] == '0')) ++si;
            if(zflag == true) res += ones;
        }
        return res;
    }
};
