class Solution:
    def maxOperations(self, s: str) -> int:
        stack = [s[0]]
        for i in range(1,len(s)):
            if s[i] == '1' or s[i] != stack[-1]:
                stack.append(s[i])
        one = 0
        ans = 0
        for x in stack:
            if x == '1':
                one += 1
            else:
                ans += one
        return ans
