class Solution {
public:
    int maxOperations(string s) {
        int numsOne = 0;
        int step = 0;
        for(int i = 0; i < s.size(); i++) {
            if(s[i] == '1') {
                numsOne++;
            } else {
                step += numsOne;
                i++;
                while(i < s.size() && s[i] == '0') {
                    i++;
                }
                i--;
            }
        }
        return step;
    }
};
