class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0, ans = 0;
        if(s[0] == '1') cnt++;
        for(int i = 1; i < s.size(); i++){
            if(s[i] == '1') cnt++;
            if(s[i] == '0' && s[i - 1] == '1') ans += cnt;
        }
        return ans;
    }
};
