class Solution {
    public int maxOperations(String s) {
        int res = 0;
        List<Integer> idxes = new ArrayList<>();
        char[] chs = s.toCharArray();
        for (int i = 0; i < chs.length; i++) {
            if (chs[i] == '1') {
                idxes.add(i);
            }
        }
        idxes.add(chs.length);
        int cnt = 0;
        for (int i = idxes.size() - 1; i > 0; i--) {
            if (idxes.get(i) - idxes.get(i - 1) > 1) {
                cnt++;
            } 
            res += cnt;
        }
        return res;
    }
}
