class Solution {
public:
    int maxOperations(string s) {

        int n = s.size();

        int sol = 0;

        int c = 0;

        bool f = false;

        for (int i = 0; i < n; ++i) {
            if (s[i] == '1') {
                if (f) {
                    sol += c;
                    f = false;
                }
                c++;
            } else {
                f = true;
            }
        }

        if (f) {
            sol += c;
        }

        return sol;

    }
};
