class Solution {
public:
    int maxOperations(string s) {
        int c=0, k=0;
        int ans = 0;
        for(int i=0; i<s.length();i++){
            if(s[i] == '1') {
                c++;
                k=0;
            }
            else{
                if(k==0) ans += c;
                k++;
            }
        }
        return ans;
    }
};
