class Solution:
    def maxOperations(self, s: str) -> int:
        s += '1'
        n = len(s)
        pre_one = -1
        count = 0
        res = 0
        for i in range(n):
            if s[i] == '1':
                if pre_one == -1:
                    pre_one = i
                else:
                    if i - pre_one > 1:
                        res += count
                    pre_one = i

                count += 1

        return res
