class Solution:
    def maxOperations(self, string: str) -> int:
        result = 0
        pointer = 0
        encountered_one = False
        binary_list = []

        for char in string:
            if char == '1':
                binary_list.append(1)
                encountered_one = True
            elif char == '0' and encountered_one:
                binary_list.append(0)
                encountered_one = False

        for bit in binary_list:
            if bit == 1:
                pointer += 1
            else:
                result += pointer

        return result
