#include<bits/stdc++.h>
using namespace std;

class Solution {
public:
    int maxOperations(string s) {

        vector<int>v;
        for(auto it:s)
        {
            if(v.empty() || v.back()==1 || it-'0'==1)
            {
                v.push_back(it-'0');
            }
        }
        int ans=0;
        int z=0;
        for(int i=v.size()-1;i>=0;i--){
            if(v[i]==0)
                z++;
            else{
                ans+=z;
            }
        }
        return ans;

    }
};
