class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int n = s.size();
        int idx = 0;
        bool tag = false;
        for(int i = 0; i < n-1; i++) {
            if(s[i] == '1' && s[i+1] == '0') {
                int j = i+1;
                while(j < n && s[j] == '0') j += 1;
                idx += 1;
                ans += idx;
                i = j-1;
            }else if(s[i] == '1') idx += 1;
        }
        return ans;
    }
};
