class Solution {
public:
    int maxOperations(string s) 
    {
        vector<pair<char,int>>v;
        v.push_back({s[0],1});
        for(int i = 1 ; i < s.size() ; i++)
        {
            if(v.back().first == s[i])
            {
                v.back().second++;
            }
            else v.push_back({s[i],1});
        }
        int ans = 0, one = 0;
        for(int i = 0 ; i < v.size() ; i++)
        {
            if(v[i].first == '1')
            {
                one += v[i].second;
            }
            else
            {
                ans += one;
            }
        }
        return ans;

    }
};
