class Solution {
public:
    int maxOperations(string s) {
      int pres=0, pnt=0;
        bool chk=false;
     string a="";
        // vector<int> b;
        for(auto it: s){
            if(it=='1') {
                a+='1';

                chk=true;
            }else if(it=='0' and chk){
                a+='0';

                chk=false;
            }
        }

        for(auto val: a){
          if(val=='1') pnt++;
        else pres+=pnt;
        }
        return pres;
    }
};
