class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int n = s.length();
        int zero_block = 0;
        for (int i = n-1; i>=0; i--) {
            if(s[i] == '1') {
                ans += zero_block;
            } else {
                if(i == n-1 || s[i+1] != '0') {
                    zero_block++;
                }
            }

        }
        return ans;
    }
};
