        for(int i=0;i<n;i++)
        {
            if(s[i]=='1')
            temp.push_back(i);
        }
        int sz=(int)(temp.size());
        long long ans=0;
        int lst=n-1;
        int cmt=0;
        int idx=-1;
        for(int i=sz-1;i>=0;i--)
        {
            if(temp[i]==lst)
