            }
            else
            {
              idx=temp[i];
               break;
            }
            lst--;
        }
        vector<long long> sv;
        for(int i=0;i<=idx;i++)
        {
            int j=i;
            int cnt=0;
            while( (j<=idx) && (s[j]=='1'))
            {
                cnt++;
                j++;
            }
            i=j;
            if(cnt)
