class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        long jj = 0;
        set<int>st;
        int countz = 0;
        int i = n-1;
        while(i>=0) {
            if(s[i] == '0') {
                while(i>= 0 && s[i] == '0') {
                    i--;
                }
                countz++;
            }
            else {
                jj+=countz; 
                i--;
            }
        }
        return jj;
    }
};
