class Solution {
public:
    int maxOperations(string s) {
        int zs = 0;
        int n = s.size();
        int ans = 0;
        for (int i = n - 1; i >= 0; ) {
            if (s[i] == '0') {
                zs++;
                while (i >= 0 && s[i] == '0') {
                    i--;
                }
            } else {
                ans += zs;
                i--;
            }
        }
        return ans;
    }
};
