class Solution {
public:
    int maxOperations(string s) {
        int ans=0,f=0,o=0;
       for(int i=0;i<s.length();i++){
        if(s[i]=='0'){
            f=1;
        }
        else{
            if(f==0){
                o++;
            }
            else{
                ans+=o;
                o++;
                f=0;
            }
        }
       }
       if(s[s.length()-1]=='0'){
        ans+=o;
       }
        return ans;

    }
};
