class Solution {
public:
    int maxOperations(string s) {
        long long ans=0;
        long long cnt=0;
        int last='0';
        for(auto i:s)
        {
            if(i=='1')cnt++;
            if(i=='0'&&last!='0')ans+=cnt;
            last=i;
        }
        return ans;
    }
};
