class Solution {
public:
    int maxOperations(string s) {
        int zero = 0;
        int n = s.size();
        int ans=0;
        for(int i=0;i<n;i++){
            if(s[i]=='0'){
                if(i==0 || s[i-1]!='0')
                ans+=zero;
            }
            else{
                zero++;
            }
        }
        return ans;
    }
};
