class Solution:
    def maxOperations(self, s: str) -> int:
        s = s[::-1]
        sum_buffer = []
        state = 0
        for c in s:
            if c=='0':
                state=1
            elif state==1 and c=='1':
                state=2
                sum_buffer.append(1)
            elif state==2 and c=='1':
                sum_buffer[-1]+=1
        ret = 0
        for i,c in enumerate(sum_buffer):
            ret += (i+1)*c
        # print(sum_buffer)
        return ret
