class Solution:
    def maxOperations(self, s: str) -> int:
        cnt, ans = 0, 0
        i = 0
        while i < len(s):
            if s[i] == '1':
                cnt += 1
            else:
                while i < len(s) - 1 and s[i + 1] == '0':
                    i += 1
                ans += cnt
            i += 1
        return ans
