class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        // while(x>=0 && s[x]=='1') x--;
        int cnt=0,ans=0;
        for(int i=0;i<n;i++){
            if(s[i]=='0') continue;
            int start=i;
            while(i<n && s[i]=='1') i++;
            if(i==n) break;
            cnt+=i-start;
            ans+=cnt;
        }
        return ans;
    }
};
